          if ((result['workflow_name'] == WF_variable_mapping[key]['flow_name'])):
            if result['workflow_version'] <= WF_variable_mapping[key]["flow_version"]:
              print(f"Version on SNOW: {result['workflow_version']} \nFlow Version in Mapping: {WF_variable_mapping[key]['flow_version']} \nSource: {result['workflow_source']}\nPassing on the Data model to SNOW now.")
              mapped_variables = list(WF_variable_mapping[key]['variables'].keys())
            else:
              print(f"The version to send is lower than the version existing on SNOW, hence terminating the WF.\nThe Version on SNOW: {result['workflow_version']}\n The version sent by SXO: {WF_variable_mapping[key]['flow_version']}")
              mapped_variables = []
              sys.exit()



    #now we will get the list of variables from SXO WF
    variable_list = []
    for i in WF_Details.get('variables'):
        variable_list.append(i.get('properties').get('name'))
    # print(f"The variable list from SXO WF : {variable_list}")

    for variable in mapped_variables:
        if variable in variable_list:
            validation_flag = True
