            validation_flag = False
    # print(f"validation_flag: {validation_flag}\n")


    #Since WF has all the parameters from our mapping, we are proceeding to build payload and send it out to SNOW.
    snow_payload = []

    #build Data Model
    data_model = []
    for i in list(WF_variable_mapping[key]['variables'].items()):
        # print(i,'\n')
        variable_body = {
            "name": i[0]
        }
        for j in i[1]:
            variable_body[j] = i[1][j]

        #get default values for each item
        for k in WF_Details.get('variables'):
            if k.get('properties').get('name') == i[0]:
                variable_body['default'] =  str(k.get('properties').get('value'))
        # print(variable_body, '\n')
        # variable_body = json.dumps(variable_body)
        data_model.append(variable_body)

    #build the body for payload
    body  = {
        "workflow_version": WF_variable_mapping[key]['flow_version'],
        "workflow_name": WF_variable_mapping[key]['flow_name'],
        "bpmn_flow_url": "https://securex-ao.us.security.cisco.com/orch-ui/workflow/$activity.definition_activity_01UVYZX2P9W5D1Tc7khXTBU0hC1ObJVK1Dy.output.script_queries.WF_ID $/",
        "workflow_source": "SXO",
        "short_description": WF_variable_mapping[key]['description'],
        "data_model": data_model,
        "workflow_id": "$activity.definition_activity_01UVYZX2P9W5D1Tc7khXTBU0hC1ObJVK1Dy.output.script_queries.WF_ID $"
             }
    # body = json.dumps(body)
    snow_payload.append(body)
    snow_payload = json.dumps(snow_payload)

    print(snow_payload)
except:
    import traceback
    snow_payload = None
    print(f" The WF name is : {key} \n The WF ID is : {wf_id} \n BYPASSING THIS WF")
    traceback.print_exc()
