properties.script:
wait_timer = $workflow.definition_workflow_01TNMCZZV7D3E5O8XIJc2tqJIjOfKRHxdwa.local.variable_workflow_01USIOI3D2YW61ue3eSJmH4GG2buy7kunZL$
wait_timer_unit = "$workflow.definition_workflow_01TNMCZZV7D3E5O8XIJc2tqJIjOfKRHxdwa.local.variable_workflow_01USIOSOVJCON6hhGfTRO17ErGOrO05lzLh$"

if wait_timer_unit.lower() == 'minutes':
    wait_time = wait_timer*60
elif wait_timer_unit.lower() == 'hours':
    wait_time = wait_timer*60*60
elif wait_timer_unit.lower() == 'days':
    wait_time = wait_timer*24*60*60
else:
    wait_time = wait_timer

wait_time_minute = round(wait_time/60) + 1

import re

CLIoutput = $activity.definition_activity_01V5BG5K9IQKK6KgIlklKZWjf9y9gNI7hZA.output.variable_workflow_01UTK0DDWWW5C0KAKyevB4TFdxEfG4vUp8a$

impacted_neighbor_ip = ''
cli_output = ''
state = ''

try:
  output = CLIoutput['TASK_RESULT'][0]['command_results']['neighbor_status']['output']
  if (output) and (output.split('\r\n')[1]):
    cli_output = repr(output)[1:-1]
    match = re.search("$workflow.definition_workflow_01TNMCZZV7D3E5O8XIJc2tqJIjOfKRHxdwa.local.variable_workflow_01TZZUWEF0GYH34bRfnpHnK11xkNdbEpZxO$",output.split('\r\n')[1])
  if match:
    impacted_neighbor_ip = str(match.group(0))

  if "$workflow.definition_workflow_01TNMCZZV7D3E5O8XIJc2tqJIjOfKRHxdwa.local.variable_workflow_01TZZUWEF0GYH34bRfnpHnK11xkNdbEpZxO$" == impacted_neighbor_ip:
    state = "established"
  else:
    state = "not established"

except:
    pass
